const Solutions = () => {
  const [selectedSolution, setSelectedSolution] = useState('Strategy');

  const isSelectedSolution = (solution: any) => selectedSolution === solution;

  useEffect(() => {
    // Fetch the message from the API route
    fetch('/api/hello')
      .then(response => response.json())
      .then(data => {
        console.log(data);
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);


  return (
    <div className={styles.wrapper}>
      <div className={styles.titleWrapper}>
        <div className={styles.title}>Solutions</div>
        <div className={styles.square} />
      </div>
      <div className={styles.solutionList}>
        {solutionList.map((solution) => {
          const isSelected = isSelectedSolution(solution.title);
          return (
            <div
              className={`${styles.solution} ${
                isSelected && styles.selectedSolution
              }`}
              key={solution.title}
              onMouseEnter={() => setSelectedSolution(solution.title)}
              onMouseLeave={() => setSelectedSolution('')}
            >
              <div className={styles.solutionIcon}>{solution.icon}</div>
              <div className={styles.solutionTitle}> {solution.title}</div>
              {isSelected && (
                <div className={styles.solutionDescription}>
                  {solution.description}
                </div>
              )}
            </div>
          );
        })}
      </div>
      <div className={styles.blackSquare} />
    </div>
  );
};

export default Solutions;
